mapper.select:
select
id, `name`, s_no jobNo, phone, prov_code provCode, city_code cityCode, area_code areaCode, address,
enable_status enableStatus,created_date createdDate
from d_salesman ds
where ds.del_flag = 0

    and name = #{name}


    and DATE_FORMAT(created_date, '%Y-%m-%d') >= #{sDate}
    and DATE_FORMAT(created_date, '%Y-%m-%d') <= #{eDate}

select ds.id, ds.name, s_no jobNo, phone, bp.name prov, bc.name city, ba.name area, address, ds.enable_status enableStatus,ds.created_date createdDate from d_salesman ds left join base_provinces bp on ds.prov_code = bp.code left join base_cities bc on ds.city_code = bc.code left join base_areas ba on ds.area_code = ba.code where ds.del_flag = 0
and ds.name = #{name}
and DATE_FORMAT(ds.created_date, '%Y-%m-%d') >= #{sDate} and DATE_FORMAT(ds.created_date, '%Y-%m-%d') <= #{eDate}
